function imgtodata(){$('img[data-image-gif=true]').each(function(){var self=$(this);var data=self.data();if(self.width()/self.height()!=data.imageW/data.imageH&&(self.width()!=data.imageW||self.height()!=data.imageH)){var pw=self.parent().width();var h=pw/data.imageW*data.imageH;var el=$('<div>',{class:"replaced_image",css:{background:'url(\''+self.attr('src')+'\') 50% 50% no-repeat',"background-size":(self.width()/self.height()<data.imageW/data.imageH)?"100% auto":"auto 100%"},html:$('<img>',{src:"/thumb/"+data.imageW+"x"+data.imageH+"/notfound.png",css:{opacity:0}})});el.data(data);self.before(el);self.remove();}});}